function improvementsController(n,t,i){n.minimize=!1;n.open=!0;n.suggestion="";n.closeWindow=function(){n.open=!1};n.minimizeWindow=function(){n.minimize=!n.minimize};n.sendSuggestion=function(){if(!(!n.suggestion.length>5)){let r=new Improvement;r.value=n.suggestion;t.postImprovement(r).then(function(){i.success("Thank you! :)")},function(n){console.log(n)}).finally(function(){n.suggestion=""})}}}app.controller("improvementsController",["$scope","improvementsService","toastr",improvementsController]);